package cn.hm1006.customer.controller;import cn.hm1006.customer.entity.Customer;import cn.hm1006.customer.service.ICustomerService;import cn.hm1006.customer.vo.TableDataVo;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;import javax.annotation.Resource;import java.util.Map;/** * @author : Two * @version : v1.0 * @description TODO * @date : 2021/3/2 19:58 */@RestController@RequestMapping("/sys")public class CustomerController {    @Resource    private ICustomerService customerService;    @RequestMapping("/customer/table")    public String toCustomer(@RequestParam Map<String,Object> map){        String json = customerService.searchAll(map);        return json;    }    @RequestMapping("/customer/add")    public TableDataVo addCustomer(Customer customer){        try {        customerService.addCustomer(customer);        return new TableDataVo(0,"添加成功");        }catch (Exception e){            e.printStackTrace();            return new TableDataVo(1,"添加失败");        }    }}