package cn.hm1006.search.controller;import cn.hm1006.search.entity.Customer;import cn.hm1006.search.service.ISearchService;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import javax.annotation.Resource;import java.io.IOException;import java.lang.reflect.InvocationTargetException;import java.util.HashMap;/** * @author : Two * @version : v1.0 * @description TODO * @date : 2021/3/2 21:24 */@RestController@RequestMapping("/search")public class SearchController {    @Resource    private ISearchService searchService;    @PostMapping(value = "/table",produces = "application/json;charset=UTF-8")    public String searchTable(@RequestBody HashMap<String,Object> map){        return searchService.searchByCondition(map);    }    @PostMapping(value = "/add",produces = "application/json;charset=UTF-8")    public void addCustomer(@RequestBody Customer customer) throws InvocationTargetException, NoSuchMethodException, IOException, IllegalAccessException {        searchService.addCustomer(customer);    }}