package cn.hm1006.search.utils;import com.fasterxml.jackson.core.JsonProcessingException;import com.fasterxml.jackson.databind.ObjectMapper;/** * @author : Two * @version : v1.0 * @description TODO * @date : 2021/3/2 21:12 */public class JSON {        public static String toJson(Object object){            ObjectMapper mapper = new ObjectMapper();            String json = null;            try {                json = mapper.writeValueAsString(object);            } catch (JsonProcessingException e) {                e.printStackTrace();            }            return json;        }}