package cn.hm1006.search.service.impl;import cn.hm1006.search.entity.Customer;import cn.hm1006.search.service.ISearchService;import org.junit.Test;import org.junit.runner.RunWith;import org.springframework.boot.test.context.SpringBootTest;import org.springframework.test.context.junit4.SpringRunner;import javax.annotation.Resource;import java.io.IOException;import java.lang.reflect.InvocationTargetException;import java.util.HashMap;import static org.junit.Assert.*;/** * @author : Two * @version : v1.0 * @description TODO * @date : 2021/3/2 20:42 */@RunWith(SpringRunner.class)@SpringBootTestpublic class SearchServiceImplTest {    @Resource    private ISearchService searchService;    @Test    public void searchByCondition() {        HashMap<String, Object> map = new HashMap<>();        map.put("page",1);        map.put("size",5);        map.put("name","海尔");        map.put("state",1);        searchService.searchByCondition(map);    }    @Test    public void addCustomer() {        Customer customer = new Customer(7,"hot","123","111",100000L,"中国北京",1);        try {            searchService.addCustomer(customer);        } catch (IllegalAccessException e) {            e.printStackTrace();        } catch (NoSuchMethodException e) {            e.printStackTrace();        } catch (InvocationTargetException e) {            e.printStackTrace();        } catch (IOException e) {            e.printStackTrace();        }    }}